import * as offer from '../offer-store/reducers/offer.reducers';
import { createFeatureSelector } from '@ngrx/store';

export interface AppState {
    authState: auth.State;
    offer: offer.State;
}

export const reducers = {
    auth: auth.reducer,
    offer: offer.reducer

};

export const selectAuthState = createFeatureSelector<AppState>('auth');
export const selectOfferState = createFeatureSelector<AppState>('offer');

import { Offer } from '../../models/offer.model';
import { All, GET_OFFER, LOAD_OFFER, ADD_OFFER, ADD_SUCCESS, UPDATE_OFFER, UPDATE_SUCCESS, DELETE_OFFER, DELETE_SUCCESS } from '../actions/offer.actions';

export interface State {
    offer: Offer[];
    success: boolean | null;
    message: string | null;
}

export const initialState: State = {
    offer: [],
    message: null,
    success: null
};

export function reducer(state = initialState, action: All): State {
    switch (action.type) {
        case GET_OFFER: {
            return {
                ...state,
            };
        }
        case LOAD_OFFER: {
            return {
                ...state,
                offer: action.payload,
                message: null,
            };
        }
        case ADD_OFFER: {
            return {
                ...state
            };

        }
        case ADD_SUCCESS: {
            return {
                ...state,
                message: 'Offer added successfully!',
            };
        }
        case UPDATE_OFFER: {
            return {
                ...state
            };

        }
        case UPDATE_SUCCESS: {
            return {
                ...state,
                message: 'Offer updated successfully!',
            };
        }
        case DELETE_OFFER: {
            return {
                ...state
            };

        }
        case DELETE_SUCCESS: {
            return {
                ...state,
                message: 'Offer deleted successfully!',
            };
        }
        default: {
            return state;
        }
    }
}

import { User } from '../../models/user';
import { AuthActionTypes, All } from '../actions/auth.actions';


export interface State {
    isAuthenticated: boolean;
    user: User | null;
    errorMessage: string | null;
}

export const initialState: State = {
    isAuthenticated: false,
    user: null,
    errorMessage: null
};

export function reducer(state = initialState, action: All): State {
    switch (action.type) {
        case AuthActionTypes.LOGIN_SUCCESS: {
            return {
                ...state,
                isAuthenticated: true,
                user: {
                    email: action.payload.email
                },
                errorMessage: null
            };
        }
        case AuthActionTypes.LOGIN_FAILURE: {
            return {
                ...state,
                errorMessage: 'Incorrect email and/or password.'
            };
        }
        case AuthActionTypes.REGISTER_SUCCESS: {
            return {
                ...state,
                isAuthenticated: true,
                user: {
                    email: action.payload.email
                },
                errorMessage: null
            };
        }
        case AuthActionTypes.REGISTER_FAILURE: {
            return {
                ...state,
                errorMessage: 'That email is already in use.'
            };
        }
        case AuthActionTypes.LOGOUT: {
            return initialState;
        }
        default: {
            return state;
        }
    }
}

