import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { Actions, Effect, ofType } from '@ngrx/effects';
import { Observable } from 'rxjs';
import { map, switchMap, tap } from 'rxjs/operators';
import { AuthService } from '../../services/auth.service';
import {
    AuthActionTypes,
    LogIn, LogInSuccess, LogInFailure,
    Register, RegisterSuccess, RegisterFailure,
    LogOut,
} from '../actions/auth.actions';

@Injectable()
export class AuthEffects {

    constructor(
        private actions: Actions,
        private authService: AuthService,
        private router: Router,
    ) { }

    @Effect()
    LogIn: Observable<any> = this.actions.pipe(
        ofType(AuthActionTypes.LOGIN),
        map((action: LogIn) => action.payload),
        switchMap(payload => {
            return this.authService.login({ email: payload.email, password: payload.password }).pipe(
                map((user) => {
                    if (user.length > 0) {
                        return new LogInSuccess({ email: payload.email });
                    } else {
                        return new LogInFailure({ error: 'Invalid credentials' });
                    }
                }));
        }));

    @Effect({ dispatch: false })
    LogInSuccess: Observable<any> = this.actions.pipe(
        ofType(AuthActionTypes.LOGIN_SUCCESS),
        tap((user) => {
            localStorage.setItem('user', JSON.stringify(user.payload));
            this.router.navigateByUrl('/');
        })
    );

    @Effect({ dispatch: false })
    LogInFailure: Observable<any> = this.actions.pipe(
        ofType(AuthActionTypes.LOGIN_FAILURE)
    );

    @Effect()
    Register: Observable<any> = this.actions.pipe(
        ofType(AuthActionTypes.REGISTER),
        map((action: Register) => action.payload),
        switchMap(payload => {
            return this.authService.register(payload).pipe(
                map((result) => {
                    return new RegisterSuccess(result);
                }));
        }));

    @Effect({ dispatch: false })
    RegisterSuccess: Observable<any> = this.actions.pipe(
        ofType(AuthActionTypes.REGISTER_SUCCESS),
        tap((user) => {
            this.router.navigate(['/login']);
        })
    );

    @Effect({ dispatch: false })
    RegisterFailure: Observable<any> = this.actions.pipe(
        ofType(AuthActionTypes.REGISTER_FAILURE)
    );

    @Effect({ dispatch: false })
    public LogOut: Observable<any> = this.actions.pipe(
        ofType(AuthActionTypes.LOGOUT),
        tap((user) => {
            localStorage.removeItem('token');
        })
    );
}

import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { User } from '../models/user';
import { Observable, throwError } from 'rxjs';
import { retry, catchError } from 'rxjs/operators';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  BASE_URL = environment.baseURL;

  constructor(private http: HttpClient) { }

  login(user: User): Observable<User[]> {
    const url = encodeURI('users?email=' + user.email + '&password=' + user.password);
    return this.http.get<User[]>(this.BASE_URL + url).pipe(
      retry(1),
      catchError(this.handleError)
    );
  }

  register(user: User): Observable<User> {
    const httpOptions = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json'
      })
    };
    const url = encodeURI('users');
    return this.http.post<User>(this.BASE_URL + url, user, httpOptions).pipe(
      retry(1),
      catchError(this.handleError)
    );
  }

  // tslint:disable-next-line: typedef
  handleError(error) {
    let errorMessage = '';
    if (error.error instanceof ErrorEvent) {
      errorMessage = `Error: ${error.error.message}`;
    } else {
      errorMessage = `Error Code: ${error.status}\nMessage: ${error.message}`;
    }
    window.alert(errorMessage);
    return throwError(errorMessage);
  }

}

